import React, { useEffect, useState } from "react";
import {
  Button,
  Form,
  Input,
  Container,
  Row,
  Col,
  FormGroup,
  Label,
  Alert
} from "reactstrap";
import { connect } from "react-redux";
import LandingInstManager from "../LandingInstManager";

const TabelaPontosAlunos = (props) => {
  const { idGuerra, idInstituicao } = props;

  const [alunos, setAlunos] = useState();

  useEffect(async () => {
    await obterDadosAlunos();
  }, []);

  const obterDadosAlunos = async () => {
    await LandingInstManager.ObterAlunos().then(async (response) => {
      setAlunos(response);
    });
  };

  return (
    <>
      <div className="container">
        <h1>Tabela est√°tica de estudantes</h1>
        <table>
          <thead>
            <tr>
              <th>nome</th>
              <th>turma</th>
              <th>email</th>
            </tr>
          </thead>
          <tbody>
            {alunos.map((aluno) => (
              <tr key={aluno._id}>
                <td>{aluno.nome}</td>
                <td>{aluno.turma}</td>
                <td>{aluno.email}</td>
                <td />
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default TabelaPontosAlunos;
