import React, { useEffect, useState } from "react";
import { Button, Table } from "reactstrap";
import { connect } from "react-redux";
import LandingInstManager from "../LandingInstManager";

const TabelaPontosAlunos = (props) => {
  const { idGuerra, idInstituicao } = props;

  const [alunos, setAlunos] = useState();

  useEffect(async () => {
    await obterDadosAlunos();
  }, []);

  const obterDadosAlunos = async () => {
    await LandingInstManager.ObterAlunos().then(async (response) => {
      setAlunos(response);
    });
  };

  return (
    <>
      <Table responsive>
        <thead>
          <tr>
            <th className="text-center">Matrícula</th>
            <th>Nome</th>
            <th>Equipe</th>
            <th className="text-center">Horas estudadas</th>
            <th className="text-right">Pontos totais</th>
          </tr>
        </thead>
        <tbody>
          {alunos &&
            alunos.map((aluno) => (
              <tr key={aluno._id}>
                <td>{aluno.matricula}</td>
                <td>{aluno.nome}</td>
                <td>{aluno.idEquipe}</td>
                <td>{aluno.horasEstudadas}</td>
                <td>{aluno.horasEstudadas * 23}</td>
                <td />
              </tr>
            ))}
          <td className="text-center">1</td>
          <td>Andrew Mike</td>
          <td>Develop</td>
          <td className="text-center">2013</td>
          <td className="text-right">€ 99,225</td>
          <td className="text-right">
            <Button className="btn-icon" color="info" size="sm">
              <i className="fa fa-user"></i>
            </Button>
            {` `}
            <Button className="btn-icon" color="success" size="sm">
              <i className="fa fa-edit"></i>
            </Button>
            {` `}
            <Button className="btn-icon" color="danger" size="sm">
              <i className="fa fa-times" />
            </Button>
          </td>
        </tbody>
      </Table>
      <div className="container">
        <h1>Tabela estática de estudantes</h1>
        <table>
          <thead>
            <tr>
              <th>nome</th>
              <th>turma</th>
              <th>email</th>
            </tr>
          </thead>
          <tbody>
            {alunos &&
              alunos.map((aluno) => (
                <tr key={aluno._id}>
                  <td>{aluno.nome}</td>
                  <td>{aluno.turma}</td>
                  <td>{aluno.email}</td>
                  <td />
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default TabelaPontosAlunos;
