import React, { useEffect, useState } from "react";
import classnames from "classnames";
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardImg,
  CardTitle,
  Label,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Col
} from "reactstrap";
import { Redirect } from "react-router-dom";
import CadastroInstituicaoManager from "../CadastroInstituicaoManager";
import { register } from "../../../redux/actionCreators";
import { connect } from "react-redux";

import ExamplesNavbar from "components/Navbars/ExamplesNavbar.js";
import Footer from "components/Footer/Footer.js";

const CadastroInstituicao = (props) => {
  const { message } = props;
  const [squares1to6, setSquares1to6] = useState("");
  const [squares7and8, setSquares7and8] = useState("");

  const [redirecionar, setRedirecionar] = useState(null);
  const [botaoHabilitado, setBotaoHabilitado] = useState(true);
  const [fullNameFocus, setFullNameFocus] = useState(false);
  const [descriptionFocus, setDescriptionFocus] = useState(false);
  const [loginFocus, setLoginFocus] = useState(false);
  const [emailFocus, setEmailFocus] = useState(false);
  const [passwordFocus, setPasswordFocus] = useState(false);

  const [nomeInstituicao, setNomeInstituicao] = useState("");
  const [descricao, setDescricao] = useState("");
  const [login, setLogin] = useState("");
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const [termosCondicoes, setTermosCondicoes] = useState("");
  const [sucesso, setSucesso] = useState(false);

  useEffect(() => {
    setBotaoHabilitado(
      nomeInstituicao && descricao && login && email && senha && termosCondicoes
    );
  }, [
    setBotaoHabilitado,
    nomeInstituicao,
    descricao,
    login,
    email,
    senha,
    termosCondicoes
  ]);

  const cadastrarInstituicao = async () => {
    const instituicao = {
      usuario: login,
      nome: nomeInstituicao,
      senha: senha,
      descricao: descricao,
      email: email
    };

    setSucesso(false);

    // const resultadoInstituicao = await CadastroInstituicaoManager.cadastrarInstituicao(
    //   instituicao
    // );
    // if (resultadoInstituicao) {
    //   console.log("Criado com sucesso");
    //   setRedirecionar(
    //     <Redirect to={"/pagina-inicial"} />
    //   ); /*TODO trocar para redirecionar para pagina de login*/
    // }

    props
      .dispatch(register(instituicao))
      .then(() => {
        setSucesso(true);
      })
      .catch(() => {
        setSucesso(false);
      });
  };

  useEffect(() => {
    document.body.classList.toggle("register-page");
    document.documentElement.addEventListener("mousemove", followCursor);
    // Specify how to clean up after this effect:
    return function cleanup() {
      document.body.classList.toggle("register-page");
      document.documentElement.removeEventListener("mousemove", followCursor);
    };
  }, []);

  /* Animação dos Squares */
  const followCursor = (event) => {
    let posX = event.clientX - window.innerWidth / 2;
    let posY = event.clientY - window.innerWidth / 6;
    setSquares1to6(
      "perspective(500px) rotateY(" +
        posX * 0.05 +
        "deg) rotateX(" +
        posY * -0.05 +
        "deg)"
    );
    setSquares7and8(
      "perspective(500px) rotateY(" +
        posX * 0.02 +
        "deg) rotateX(" +
        posY * -0.02 +
        "deg)"
    );
  };

  return (
    <>
      <ExamplesNavbar />
      <div className="wrapper">
        <div className="page-header">
          <div className="page-header-image" />
          <div className="content">
            <Container>
              <Row>
                <Col className="offset-lg-0 offset-md-3" lg="5" md="6">
                  <div
                    className="square square-7"
                    id="square7"
                    style={{ transform: squares7and8 }}
                  />
                  <div
                    className="square square-8"
                    id="square8"
