                        </InputGroup>
                        <InputGroup
                          className={classnames({
                            "input-group-focus": descriptionFocus
                          })}
                        >
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="tim-icons icon-notes" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            value={descricao}
                            placeholder="Descrição"
                            type="text"
                            onFocus={(e) => setDescriptionFocus(true)}
                            onBlur={(e) => setDescriptionFocus(false)}
                            onChange={(e) => setDescricao(e.target.value)}
                          />
                        </InputGroup>
                        <InputGroup
                          className={classnames({
                            "input-group-focus": loginFocus
                          })}
                        >
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="tim-icons icon-single-02" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            value={login}
                            placeholder="Login"
                            type="text"
                            onFocus={(e) => setLoginFocus(true)}
                            onBlur={(e) => setLoginFocus(false)}
                            onChange={(e) => setLogin(e.target.value)}
                          />
                        </InputGroup>
                        <InputGroup
                          className={classnames({
                            "input-group-focus": emailFocus
                          })}
                        >
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="tim-icons icon-email-85" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            value={email}
                            placeholder="Email"
                            type="text"
                            onFocus={(e) => setEmailFocus(true)}
                            onBlur={(e) => setEmailFocus(false)}
                            onChange={(e) => setEmail(e.target.value)}
                          />
                        </InputGroup>
                        <InputGroup
                          className={classnames({
                            "input-group-focus": passwordFocus
                          })}
                        >
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="tim-icons icon-lock-circle" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            value={senha}
                            placeholder="Senha"
                            type="password"
                            autoComplete="new-password"
                            onFocus={(e) => setPasswordFocus(true)}
                            onBlur={(e) => setPasswordFocus(false)}
                            onChange={(e) => setSenha(e.target.value)}
                          />
                        </InputGroup>
                        <FormGroup check className="text-left">
                          <Label check>
                            <Input
                              type="checkbox"
                              onChange={(e) =>
                                setTermosCondicoes(e.target.checked)
                              }
                            />
                            <span className="form-check-sign" />
                            Concordo com os{" "}
                            <a
                              href="#pablo"
                              onClick={(e) => e.preventDefault()}
                            >
                              termos e condições
                            </a>
                            .
                          </Label>
                        </FormGroup>
                      </Form>
                    </CardBody>
                    <CardFooter>
                      <Button
                        className="btn-round"
                        color="primary"
                        size="lg"
                        disabled={!botaoHabilitado}
                        onClick={() => cadastrarInstituicao()}
                      >
                        Começar
                      </Button>
                      <a
                        href="#pablo" /* TODO Colocar o link para a pagina do login */
                        className="ml-3"
                        onClick={(e) => e.preventDefault()}
                      >
                        Já tenho conta. Ir para login.
                      </a>
                    </CardFooter>
                  </Card>
                  <UncontrolledAlert className="alert-with-icon" color="danger">
                    <span
                      data-notify="icon"
                      className="tim-icons icon-support-17"
                    />
                    <span>
                      <b>Oh snap! -</b>
                      This is a regular notification made with ".alert-danger"
                    </span>
                  </UncontrolledAlert>
                </Col>
              </Row>
              <div className="register-bg" />
              <div
                className="square square-1"
                id="square1"
                style={{ transform: squares1to6 }}
              />
              <div
                className="square square-2"
                id="square2"
                style={{ transform: squares1to6 }}
              />
              <div
                className="square square-3"
                id="square3"
