import React, { useEffect, useState } from "react";
import classnames from "classnames";
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardImg,
  CardTitle,
  Label,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Col
} from "reactstrap";
import { Redirect } from "react-router-dom";

import ExamplesNavbar from "components/Navbars/ExamplesNavbar.js";
import Footer from "components/Footer/Footer.js";

const CriarEquipes = (props) => {
  const [squares1to6, setSquares1to6] = useState("");
  const [squares7and8, setSquares7and8] = useState("");

  const [redirecionar, setRedirecionar] = useState(null);
  const [botaoHabilitado, setBotaoHabilitado] = useState(true);

  const [numeroAlunosFocus, setNumeroAlunosFocus] = useState(false);
  const [nomeEquipesFocus, setNomeEquipesFocus] = useState(false);
  const [numeroEquipesFocus, setNumeroEquipesFocus] = useState(false);

  const [numeroAlunosPorEquipe, setNumeroAlunosPorEquipe] = useState(null);
  const [nomeEquipes, setNomeEquipes] = useState([]);
  const [numeroEquipes, setNumeroEquipes] = useState(null);

  //   useEffect(() => {
  //     setBotaoHabilitado(
  //       nomeInstituicao && descricao && login && email && senha && termosCondicoes
  //     );
  //   }, [
  //     setBotaoHabilitado,
  //     nomeInstituicao,
  //     descricao,
  //     login,
  //     email,
  //     senha,
  //     termosCondicoes
  //   ]);

  //   const cadastrarInstituicao = async () => {
  //     const instituicao = {
  //       usuario: login,
  //       nome: nomeInstituicao,
  //       senha: senha,
  //       descricao: descricao,
  //       email: email
  //     };

  //     const resultadoInstituicao = await CadastroInstituicaoManager.cadastrarInstituicao(
  //       instituicao
  //     );
  //     if (resultadoInstituicao) {
  //       console.log("Criado com sucesso");
  //       setRedirecionar(
  //         <Redirect to={"/pagina-inicial"} />
  //       ); /*TODO trocar para redirecionar para pagina de login*/
  //     }
  //   };

  useEffect(() => {
    document.body.classList.toggle("register-page");
    document.documentElement.addEventListener("mousemove", followCursor);
    // Specify how to clean up after this effect:
    return function cleanup() {
      document.body.classList.toggle("register-page");
      document.documentElement.removeEventListener("mousemove", followCursor);
    };
  }, []);

  /* Animação dos Squares */
  const followCursor = (event) => {
    let posX = event.clientX - window.innerWidth / 2;
    let posY = event.clientY - window.innerWidth / 6;
    setSquares1to6(
      "perspective(500px) rotateY(" +
        posX * 0.05 +
        "deg) rotateX(" +
        posY * -0.05 +
        "deg)"
    );
    setSquares7and8(
      "perspective(500px) rotateY(" +
        posX * 0.02 +
        "deg) rotateX(" +
        posY * -0.02 +
        "deg)"
    );
  };

  const geraCamposNomeEquipe = () => {
    const inputs = [];

    for (let i = 1; i <= numeroEquipes; i++) {
      inputs.push(
        <Input
          value={nomeEquipes[i]}
          placeholder="Nome da equipe"
          type="text"
          onFocus={(e) => setFullNameFocus(true)}
          onBlur={(e) => setFullNameFocus(false)}
          onChange={(e) => setNomeEquipes(nomeEquipes.append(e.target.value))}
        />
      );
    }
    return inputs;
  };

  return (
    <>
      <div className="wrapper">
        <div className="page-header">
          <div className="page-header-image" />
          <div className="content">
            <Container>
              <Row>
                <Col className="offset-lg-0 offset-md-3" lg="5" md="6">
                  <h3 className="title">
                    Criar equipes para a guerra de estudos
                  </h3>
                  <Form className="form">
                    <Input
                      value={numeroEquipes}
                      placeholder="Número total de equipes"
                      type="number"
                      onFocus={(e) => setLoginFocus(true)}
                      onBlur={(e) => setLoginFocus(false)}
                      onChange={(e) => setNumeroEquipes(e.target.value)}
                    />
                    <Input
                      value={login}
                      placeholder="Número de alunos por equipe"
                      type="number"
                      onFocus={(e) => setLoginFocus(true)}
                      onBlur={(e) => setLoginFocus(false)}
                      onChange={(e) => setLogin(e.target.value)}
                    />
                  </Form>
                  <Card className="card-register">
                    <CardHeader>
                      <CardImg
                        alt="..."
                        src={require("assets/img/square-purple-1.png").default}
                      />
                      <CardTitle tag="h4">Registro</CardTitle>
                    </CardHeader>
                    <CardBody>
                      <Form className="form">
                        <InputGroup
                          className={classnames({
                            "input-group-focus": fullNameFocus
                          })}
                        >
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="tim-icons icon-bank" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            value={nomeInstituicao}
                            placeholder="Nome da instituição"
                            type="text"
                            onFocus={(e) => setFullNameFocus(true)}
                            onBlur={(e) => setFullNameFocus(false)}
                            onChange={(e) => setNomeInstituicao(e.target.value)}
                          />
                        </InputGroup>
                        <InputGroup
                          className={classnames({
                            "input-group-focus": descriptionFocus
                          })}
                        >
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="tim-icons icon-notes" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            value={descricao}
                            placeholder="Descrição"
                            type="text"
                            onFocus={(e) => setDescriptionFocus(true)}
                            onBlur={(e) => setDescriptionFocus(false)}
                            onChange={(e) => setDescricao(e.target.value)}
                          />
                        </InputGroup>
                        <InputGroup
                          className={classnames({
                            "input-group-focus": loginFocus
                          })}
                        >
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="tim-icons icon-single-02" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            value={login}
                            placeholder="Login"
                            type="text"
                            onFocus={(e) => setLoginFocus(true)}
                            onBlur={(e) => setLoginFocus(false)}
                            onChange={(e) => setLogin(e.target.value)}
                          />
                        </InputGroup>
                        <InputGroup
                          className={classnames({
                            "input-group-focus": emailFocus
                          })}
                        >
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="tim-icons icon-email-85" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            value={email}
                            placeholder="Email"
                            type="text"
                            onFocus={(e) => setEmailFocus(true)}
                            onBlur={(e) => setEmailFocus(false)}
                            onChange={(e) => setEmail(e.target.value)}
                          />
                        </InputGroup>
                        <InputGroup
                          className={classnames({
                            "input-group-focus": passwordFocus
                          })}
                        >
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="tim-icons icon-lock-circle" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            value={senha}
                            placeholder="Senha"
                            type="password"
                            autoComplete="new-password"
                            onFocus={(e) => setPasswordFocus(true)}
                            onBlur={(e) => setPasswordFocus(false)}
                            onChange={(e) => setSenha(e.target.value)}
                          />
                        </InputGroup>
                        <FormGroup check className="text-left">
                          <Label check>
                            <Input
                              type="checkbox"
                              onChange={(e) =>
                                setTermosCondicoes(e.target.checked)
                              }
                            />
                            <span className="form-check-sign" />
                            Concordo com os{" "}
                            <a
                              href="#pablo"
                              onClick={(e) => e.preventDefault()}
                            >
                              termos e condições
                            </a>
                            .
                          </Label>
                        </FormGroup>
                      </Form>
                    </CardBody>
                    <CardFooter>
                      <Button
                        className="btn-round"
                        color="primary"
                        size="lg"
                        disabled={!botaoHabilitado}
                      >
                        Começar
                      </Button>
                      <a
                        href="#pablo" /* TODO Colocar o link para a pagina do login */
                        className="ml-3"
                        onClick={(e) => e.preventDefault()}
                      >
                        Já tenho conta. Ir para login.
                      </a>
                    </CardFooter>
                  </Card>
                </Col>
              </Row>
              <div className="register-bg" />
            </Container>
          </div>
        </div>
        <Footer />
        {redirecionar}
      </div>
    </>
  );
};

export default CriarEquipes;
